$(document).ready(function(){var e=$("#city-name").text();$.get("http://api.openweathermap.org/data/2.5/weather?q="+e+",es&lang=es",function(e){var n=e.weather[0].description,r=e.weather[0].icon,a=t(e.main.temp)+" \xbaC",i=e.main.pressure+" hPa",p=e.main.humidity+" %",o=e.wind.speed+" mps";$("#description").text(n),$("#temperature").text(a),$("#pressure").text(i),$("#humidity").text(p),$("#wind").text(o),$("#icon").attr("src","http://openweathermap.org/img/w/"+r+".png")});var t=function(e){return Math.round(e-273.15)};$("#menu-toggle").click(function(e){e.preventDefault(),$("#wrapper").toggleClass("toggled")})});